mapper.update:
update hd_admin
name = #{name,jdbcType=VARCHAR}, username = #{username,jdbcType=VARCHAR}, password = #{password,jdbcType=VARCHAR}, encrypt_password = #{encryptPassword,jdbcType=VARCHAR}, enabled = #{enabled,jdbcType=TINYINT}, update_time = now()
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id,name,username,enabled from hd_admin
and name like '%${name}%' and username like '%${username}%' and enabled = #{enabled}
limit #{startindex}, 10
