SELECT av.*, u.id AS uid, u.username AS nickname, u.realname AS realname, u.phone_num AS phone, u.servephone AS 
servephone, a.`name` AS areaname, a.address AS address 
FROM hd_arearelevance av LEFT JOIN hd_user u ON av.partid = u.id LEFT JOIN hd_area a ON av.aid = a.id 

	 AND av.id = #{number} 
	 AND u.phone_num = #{phone} 
	 AND av.aid = #{state} 
	 AND av.partid = #{source} 
	 AND av.`status` = #{status} 
	 AND av.type = #{type} 
	 ${sort}

SELECT DISTINCT(partid) AS user_id FROM hd_arearelevance WHERE type = 2 AND partid NOT IN (
	SELECT DISTINCT user_id FROM hd_user_equipment
)

select ROUND(6378.138*2*ASIN(SQRT(POW(SIN((34.8149006*PI()/180-lat*PI()/180)/2),2)+ COS(34.8149006*PI()/180)*COS(lat*PI()/180)*POW(SIN((113.568114*PI()/180- lon*PI()/180)/2),2)))*1000) AS distance , IFNULL(s.freenum,0) as freenum,a.id,a.`name` FROM hd_area a LEFT JOIN (SELECT ap.aid, COUNT(CASE WHEN ap.port_status = 1 THEN 1 END) as freenum FROM hd_allportstatus ap GROUP BY ap.aid) s on s.aid = a.id HAVING distance <= #{distance}
order by distance
LIMIT #{startnum}, 10
