mapper.select:
SELECT  e.*,a.address,a.name areaName
FROM hd_equipment_new  e  LEFT JOIN
hd_area a ON e.aid = a.id

	 AND t.user_id = #{uid} 
	 AND e.`code` LIKE '%${code}%' 
	 AND e.imei LIKE '%${sort}%' 
	 AND e.ccid LIKE '%${state}%' 
	 AND  e.hardversion = #{statement}
	 AND  e.softversionnum = #{number}
	 AND  e.hardversionnum = #{order}
	 AND e.bindtype = #{type} 
	 AND e.state = #{status}
	 AND e.several IN (${level}) 
	 AND e.aid = #{source} 
	 AND a.name LIKE '%${paramete}%' 
	 AND e.device_type = #{deviceType} 

 ORDER BY ${remark} 
  LIMIT #{startnumber},#{pages}
