mapper.select:
SELECT 

FROM hd_gen_detail 

	AND uid = #{uid}
	AND ordernum LIKE '%${order}%' 
	AND paysource IN (${source})
	AND create_time >= #{startTime}
	AND create_time <= #{endTime}

 ORDER BY create_time DESC
   LIMIT #{startnumber},#{pages}

SELECT g.*,u.username,u.realname,u.phone_num
FROM hd_gen_detail g LEFT JOIN hd_user u ON g.uid = u.id

	 AND g.merid = #{dealer} 
	 AND g.uid = #{equi} 
	 AND g.ordernum LIKE '%${ordernum}%' 
	AND g.paysource = #{type} 
	 AND g.create_time >= #{startTime} 
	 AND g.create_time <= #{endTime} 

 ORDER BY create_time DESC
  LIMIT #{startIndex},#{numPerPage}

SELECT g.*,u.username,u.realname,u.phone_num
FROM hd_gen_detail g LEFT JOIN hd_user u ON g.uid = u.id

	AND uid = #{uid}
	AND ordernum LIKE '%${order}%' 
	AND u.username LIKE '%${dealer}%' 
	AND u.realname LIKE '%${realname}%' 
	AND u.phone_num LIKE '%${mobile}%' 
	AND paysource IN (${source})
	AND create_time >= #{startTime}
	AND create_time <= #{endTime}

 ORDER BY create_time DESC
   LIMIT #{startnumber},#{pages}
