mapper.select:
select count(1)
from hd_meruser mu
LEFT JOIN hd_user u on u.id = mu.uid


		and mu.phonenum like '%${phonenum}%'


		and u.username like '%${username}%'


		and mu.enabled = #{enabled}


		and u.realname like '%${realname}%'


		and mu.province = #{province}


		and mu.city = #{city}


		and mu.country = #{country}

select mu.id,mu.phonenum,mu.`enabled`,mu.uid,mu.create_time,u.username,u.realname, da.`name` as provincename, db.`name` as cityname, dc.`name` as countryname, mu.total_earn,mu.now_earn from hd_meruser mu LEFT JOIN hd_user u on u.id = mu.uid LEFT JOIN hd_district da on da.id = mu.province LEFT JOIN hd_district db on db.id = mu.city LEFT JOIN hd_district dc on dc.id = mu.country
and mu.phonenum like '%${phonenum}%'
and u.username like '%${username}%'
and mu.enabled = #{enabled}
and u.realname like '%${realname}%'
and da.id = #{province}
and db.id = #{city}
and dc.id = #{country}
limit #{startindex},10
