mapper.select:
	SELECT COUNT(*) AS clientsnum FROM hd_user 

      	     AND merid = #{dealer} 
      	     AND aid = #{number} 
	     AND create_time >= #{startTime} 
	     AND create_time <= #{endTime}

	SELECT  u.id,u.username,b.realname,u.phone_num,u.rank,b.bankname,b.bankcardnum,b.type,b.company,b.rate 
	FROM hd_user u LEFT JOIN hd_user_bankcard b ON u.id = b.user_id 

	   and u.id = #{id} 
       and u.username = #{username} 
       and u.realname LIKE '%${realname}%' 
       and u.phone_num LIKE '%${phoneNum}%' 
       and u.rank = #{rank}
       and b.bankname LIKE '%${bankname}%'
       and b.bankcardnum LIKE '%${bankcardnum}%'

   		 ORDER BY u.create_time 
      LIMIT #{startIndex},#{numPerPage}

