mapper.update:
update hd_user set 

		earnings = earnings + #{money} 


		earnings = earnings - #{money} 

where id = #{merid}

update hd_meramount set 

		total_online_earn = total_online_earn + #{money},
		now_online_earn = now_online_earn + #{money} 


		total_online_earn = total_online_earn - #{money},
		now_online_earn = now_online_earn - #{money} 

where merid = #{merid}

mapper.select:
	SELECT  u.*, mu.username AS muusername, mu.realname AS murealname, mu.phone_num AS muphone_num, au.id AS agentid, au.username AS agentname
	FROM hd_user u LEFT JOIN hd_user mu ON u.merid = mu.id LEFT JOIN hd_user au ON u.agent_id=au.id WHERE 1 = 1  and u.rank in (0,2,3)  
     and u.username LIKE '%${username}%' 
     and u.realname LIKE '%${realname}%' 
     and u.phone_num LIKE '%${phone}%' 
     and u.id = #{uid} 
     ${paramete} 
      LIMIT #{startnumber},#{pages}

SELECT id,username,realname,phone_num,sendmoney,balance FROM hd_user
and username LIKE '%${nickname}%' and realname LIKE '%${username}%' and phone_num LIKE '%${phone}%'
${paramete} LIMIT #{startnumber},#{pages}

SELECT 

FROM hd_user
WHERE unionid = #{unionid,jdbcType=VARCHAR}

	SELECT  u.*,a.`name` AS arename,a.address,a.manid 
	FROM hd_user u LEFT JOIN hd_area a ON u.aid = a.id 

		 and u.id LIKE '%${code}%' 
		 and u.merid = #{uid} 
		 and u.aid = #{type} 
	     and u.phone_num LIKE '%${phone}%' 
		 and a.merid = #{state} 
	     and u.username LIKE '%${nickname}%' 
	     and u.realname LIKE '%${username}%' 
	     and a.`name` LIKE '%${source}%' 
		 and u.rank in (#{rank}) 
	     AND u.create_time >= #{startTime} 
		 AND u.create_time <= #{endTime} 

     ORDER BY u.balance DESC 
     ORDER BY u.balance 

      LIMIT #{startnumber},#{pages}

