	 and u.id LIKE '%${code}%' 
	 and u.merid = #{uid} 
	 and u.aid = #{type} 
     and u.phone_num LIKE '%${phone}%' 
	 and a.merid = #{state} 
     and u.username LIKE '%${nickname}%' 
     and u.realname LIKE '%${username}%' 
     and a.`name` LIKE '%${source}%' 
	 and u.rank in (#{rank}) 
     AND u.create_time >= #{startTime} 
	 AND u.create_time <= #{endTime}

